import React from "react";
import "./Cart.css";
import { useContext } from "react";
import { CartContext } from "../context/CartContext";
import { Link } from "react-router-dom";

export const Cart = () => {
    const { cart, clearCart, statePurchase } = useContext(CartContext);
    if (statePurchase) clearCart();
    if (!cart.length)
        return (
            <div>
                <h1>No hay items seleccionados</h1>
                <Link to="/">
                    <button>Ir a comprar</button>   
                </Link>
            </div>
        );
    else
        return (
            <div>
                <h1 className="carritoTitle">CARRITO</h1>
                {cart.map((item) => (
                    <div className="productsInCartContainer">
                        <div className="productInCart">
                            <h6>Producto: {item.titulo}</h6>
                        </div>
                        {item.titulo}: {item.quantity} - {item.precio}
                    </div>
                ))}
                <button onClick={clearCart}>Vaciar carrito</button>
                <Link to="/checkout">
                    <button>Siguiente</button>
                </Link>
            </div>
        );
};
